import React from 'react';
import Styled from 'styled-components';
import { Chart as ChartJs, 
    CategoryScale,
    LinearScale,
    PointElement,
    LineElement,
    Title,
    Tooltip,
    Legend,
    ArcElement,
    BarController,
} from 'chart.js';
import { Line } from 'react-chartjs-2';
import { Use_Global_Context } from '../../context/globalContext';
import { dateFormat } from '../../util/dateFormat';

ChartJs.register(
    CategoryScale,
    LinearScale,
    PointElement,
    LineElement,
    Title,
    Tooltip,
    Legend,
    ArcElement,
);

function Chart() {
    const { incomes, expenses } = Use_Global_Context();

    // Sort the income and expense data based on dates
    const sortedIncomes = incomes.sort((a, b) => new Date(a.date) - new Date(b.date));
    const sortedExpenses = expenses.sort((a, b) => new Date(a.date) - new Date(b.date));

    const incomeData = {
        labels: sortedIncomes.map((inc) => dateFormat(inc.date)),
        datasets: [
            {
                label: 'Income',
                data: sortedIncomes.map((income) => income.amount),
                backgroundColor: 'green',
                tension: 0.2,
            },
        ],
    };

    const expenseData = {
        labels: sortedExpenses.map((exp) => dateFormat(exp.date)),
        datasets: [
            {
                label: 'Expense',
                data: sortedExpenses.map((expense) => expense.amount),
                backgroundColor: 'red',
                tension: 0.2,
            },
        ],
    };

    return (
        <div className='charts'> {/* Use className='charts' */}
            <StyledChart>
                <Line data={incomeData} options={{ plugins: { title: { display: true, text: 'Income' } } }} />
            </StyledChart>
            <StyledChart>
                <Line data={expenseData} options={{ plugins: { title: { display: true, text: 'Expense' } } }} />
            </StyledChart>
        </div>
    );
}

const StyledChart = Styled.div`
    background: #F6FCF9;
    border: 2px solid #FFFFFF;
    box-shadow: 0px 1px 15px rgba(0, 0, 0, 0.06);
    padding: 1rem;
    border-radius: 20px;
    flex: 1;
    margin-right: 1rem;
    height: 400px; /* Set the desired height for the charts */
    
    &:last-child {
        margin-right: 0;
    }
`;

export default Chart;
