import React from 'react'
import Styled from 'styled-components'
import {Chart as ChartJs, 
    CategoryScale,
    LinearScale,
    PointElement,
    LineElement,
    Title,
    Tooltip,
    Legend,
    ArcElement,
    BarController,
} from 'chart.js'
import {Line} from 'react-chartjs-2'
import {Use_Global_Context} from '../../context/globalContext'
import {dateFormat} from '../../util/dateFormat'

ChartJs.register(
    CategoryScale,
    LinearScale,
    PointElement,
    LineElement,
    Title,
    Tooltip,
    Legend,
    ArcElement,
)
function chart(){
    const{incomes, expenses} = Use_Global_Context()
    const data = {
        labels: incomes.map((inc) => {
          const { date } = inc;
          return dateFormat(date);
        }),
        datasets: [
          {
            label: 'Income',
            data: incomes.map((income) => {
              const { amount } = income;
              return amount;
            }),
            backgroundColor: 'green',
            tension: 0.2,
          },
          {
            label: 'Expense',
            data: expenses.map((expense) => {
              const { amount } = expense;
              return amount;
            }),
            backgroundColor: 'red',
            tension: 0.2,
          },
        ],
      };      
    return (
        <chart_style>
            <Line data={data}/>
        </chart_style>
    )
}
const chart_style = Styled.div`
    background: #F6FCF9;
    border: 2px solid #FFFFFF;
    box-shadow: 0px 1px 15px rgba(0, 0, 0, 0.06);
    padding: 1rem;
    border-radius: 20px;
    height: 100%;
`;
export default chart;
