import React, { useState ,useMemo} from 'react';
import styled from 'styled-components';
import { MainLayout } from './styles/Layout';
import Floating from './components/Floating/Floating';
import NavigationBar from './components/Navigation/Navigation_bar';
import dash from './components/dashboard/dash';
import view_t from './components/View_transaction/view_t';
import Exp from './components/Expenses/Exp';
import Inc from './components/Incomes/Inc';
function App() {
  const [active, setActive] = useState(1);
  const display_data = () =>{
    switch(active){
        case 1:
            return <dash />
        case 2:
            return <view_t />
        case 3:
            return <Inc />
        case 4:
            return <Exp />
        default: return<dash />
    }
  }
  const Float_Memo = useMemo(()=>{
    return <Floating/>
  },[])
  return (
    <AppStyled className="App">
      {Float_Memo}
      <MainLayout>
        <NavigationBar active={active} setActive={setActive} />
        <main> 
            
        </main>
      </MainLayout>
    </AppStyled>
  );
}

const AppStyled = styled.div`
  height: 100vh;
  background: linear-gradient(90deg, #e3fdfd 0%, #A6E3E9 100%);
  position: relative;
  main{
    flex:1;
    background: #E3FDFD;
    border: 3px solid #FFFFFF;
    backdrop-filter: blur(4.5px);
    border-radius: 32px;
    overflow-x: hidden;
    &::-webkit-scrollbar{
      width: 0;
    }
  }
`;
export default App;
