import React from 'react';
import Styled from 'styled-components';
import { Chart as ChartJs, 
    CategoryScale,
    LinearScale,
    PointElement,
    LineElement,
    Title,
    Tooltip,
    Legend,
    ArcElement,
    BarController,
} from 'chart.js';
import { Line } from 'react-chartjs-2';
import { Use_Global_Context } from '../../context/globalContext';
import { dateFormat } from '../../util/dateFormat';

ChartJs.register(
    CategoryScale,
    LinearScale,
    PointElement,
    LineElement,
    Title,
    Tooltip,
    Legend,
    ArcElement,
);

function Chart() {
    const { incomes, expenses } = Use_Global_Context();

    // Prepare income data
    const incomeDataMap = new Map();
    for (const income of incomes) {
        const date = dateFormat(income.date);
        if (incomeDataMap.has(date)) {
            incomeDataMap.set(date, incomeDataMap.get(date) + income.amount);
        } else {
            incomeDataMap.set(date, income.amount);
        }
    }
    const incomeData = {
        labels: Array.from(incomeDataMap.keys()),
        datasets: [
            {
                label: 'Income',
                data: Array.from(incomeDataMap.values()),
                backgroundColor: 'green',
                tension: 0.2,
            },
        ],
    };

    // Prepare expense data
    const expenseDataMap = new Map();
    for (const expense of expenses) {
        const date = dateFormat(expense.date);
        if (expenseDataMap.has(date)) {
            expenseDataMap.set(date, expenseDataMap.get(date) + expense.amount);
        } else {
            expenseDataMap.set(date, expense.amount);
        }
    }
    const expenseData = {
        labels: Array.from(expenseDataMap.keys()),
        datasets: [
            {
                label: 'Expense',
                data: Array.from(expenseDataMap.values()),
                backgroundColor: 'red',
                tension: 0.2,
            },
        ],
    };

    return (
        <div className='charts'>
            <StyledChart>
                <h2>Total Income: {totalIncome}</h2>
                <Line data={incomeData} options={{ plugins: { title: { display: true, text: 'Income' } } }} />
            </StyledChart>
            <StyledChart>
                <h2>Total Expense: {totalExpenses}</h2>
                <Line data={expenseData} options={{ plugins: { title: { display: true, text: 'Expense' } } }} />
            </StyledChart>
        </div>
    );
}

const StyledChart = Styled.div`
    background: #F6FCF9;
    border: 2px solid #FFFFFF;
    box-shadow: 0px 1px 15px rgba(0, 0, 0, 0.06);
    padding: 1rem;
    border-radius: 20px;
    flex: 1;
    margin-right: 1rem;
    height: 400px;
    
    &:last-child {
        margin-right: 0;
    }
`;

export default Chart;
