import React from 'react'
import Styled from 'styled-components'
import { InnerLayout } from '../../styles/Layout';
import Chart from '../chart/chart';
import { dollar } from '../../util/icons';
import { Use_Global_Context } from '../../context/globalContext';
function dashboard(){
    const {totalExpenses,incomes, expenses, totalIncome, totalBalance, getIncomes, getExpenses } = Use_Global_Context

    React.useEffect(() => {
        getIncomes()
        getExpenses()
    }, [])
    return(
        <dashboard_styled>
            <InnerLayout>
                All Transactions
                <div className="stats-container">
                    <div className="chart-container">
                        <Chart />
                        <div className="amount-con">
                            <div className="income">
                                <h2>Total Income</h2>
                                <p>
                                    {dollar} {totalIncome()}
                                </p>
                            </div>
                            <div className="expense">
                                <h2>Total Expense</h2>
                                <p>
                                    {dollar} {totalExpenses()}
                                </p>
                            </div>
                            <div className="balance">
                                <h2>Total Balance</h2>
                                <p>
                                    {dollar} {totalBalance()}
                                </p>
                            </div>
                        </div>
                    </div>
                </div>
            </InnerLayout>
        </dashboard_styled>
    )
}
const dashboard_styled = Styled.div`
    display: flex;
    justify-content: space-between;
    gap: 2rem;
`;
export default dashboard;
